import React from 'react';
import  classes from './Item.module.css';
import {
  MDBCard,
  MDBCardImage,
  MDBCardBody,
  MDBCardTitle,
  MDBCardText,
  MDBCardFooter,
} from 'mdb-react-ui-kit';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'


const Item = ({product}) => {
    return (
      <MDBCard className='h-100'>
          <MDBCardImage
            src={product.productImageUrl}
            alt='...'
            position='top'
            width='150px'
            height='300px'
          />
          <MDBCardBody>
            <MDBCardTitle>{product.name}</MDBCardTitle>
            <MDBCardText>
              {product.description}
              <h4>{product.price}$<FontAwesomeIcon icon="fa-solid fa-coffee" size="xs" /></h4>
            </MDBCardText>
          </MDBCardBody>
          <MDBCardFooter>
            <button className={classes.add} >Add to cart</button>
          </MDBCardFooter>
        </MDBCard> 
    );
  };
  
  export default Item;
